const GenerateUUID = () => {
  const chars = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ'
  let result = ''
  for (let i = 0; i < 10; i++) {
    const r = Math.floor(Math.random() * chars.length)
    result += chars[r]
  }
  return result
}
const isScanning = ref(false)
const error = ref('')
const teacherInfo = ref({})

const logout = () => {
  sessionStorage.removeItem('idnt_code')
  sessionStorage.removeItem('student')
  sessionStorage.removeItem('t_idnt_code')
  sessionStorage.removeItem('teacher')
  window.location.href = '/login'
}

const startScan = () => {

  if (!amountInput.value) {
    alert('출금액을 입력해주세요')
    return
  }
  if (!selectedStudent.value) {
    alert('출금할 학생을 선택해주세요')
    return
  }

  if (amountInput.value > memberPoint.value) {
    alert('잔액이 부족합니다.')
    return
  }

  isScanning.value = true
  error.value = ''
}

const onDetect = (detectedCodes) => {
  const url = detectedCodes[0]?.rawValue
  if (url === sessionStorage.getItem('t_idnt_code')) {
    handleDeposit()
  } else {
    isScanning.value = false
    return alert('인증오류')
  }
  if (!url) return

  isScanning.value = false
  //   router.push('/sign/' + url)
}

const onError = (err) => {
  error.value = '카메라 접근 실패'
  console.error(err)
