const isUploadModalOpen = ref(false)
const uploadedFile = ref(null)

const handleFileUpload = async (e) => {
  const file = uploadedFile.value
  if (!file) {
    alert('파일을 선택해주세요')
    return
  }

  const reader = new FileReader()
  reader.onload = async (evt) => {
    const data = new Uint8Array(evt.target.result)
    const workbook = XLSX.read(data, { type: 'array' })
    const firstSheetName = workbook.SheetNames[0]
    const worksheet = workbook.Sheets[firstSheetName]

    // 엑셀 -> JSON 변환
    const jsonData = XLSX.utils.sheet_to_json(worksheet, { defval: '' })

    // 여기서 필요한 컬럼명 매핑 (엑셀 헤더에 따라 다름)
    const students = jsonData.map(row => ({
      idnt_code: GenerateUUID(), // JS에서 UUID 생성하거나 서버에서 처리
      student_name: row['이름'],
      student_grade: row['학년'],
      student_class: row['반'],
      mb_point: row['포인트'] || 0,
      student_number: row['학번'],
      birth_date: row['생년월일'], // YYYY-MM-DD 형식
      gender: row['성별'],
      guardian_name: row['보호자'],
      guardian_phone: row['연락처'],
      address: row['주소'],
      teacher: JSON.parse(sessionStorage.getItem('teacher'))?.idx,
      pay_name: row['화폐이름'],
      role_code: null
    })).concat([
      {
        idnt_code: GenerateUUID(),
        student_name: '선생님',
        student_grade: null,
        student_class: null,
        mb_point: 0,
        student_number: 0,
        birth_date: null,
        gender: null,
        guardian_name: null,
        guardian_phone: null,
        address: null,
        teacher: JSON.parse(sessionStorage.getItem('teacher'))?.idx,
        pay_name: null,
        role_code: '1'
      },
      {
        idnt_code: GenerateUUID(),
        student_name: '관리자',
        student_grade: null,
        student_class: null,
        mb_point: 0,
        student_number: 0,
        birth_date: null,
        gender: null,
        guardian_name: null,
        guardian_phone: null,
        address: null,
        teacher: JSON.parse(sessionStorage.getItem('teacher'))?.idx,
        pay_name: null,
        role_code: '1'
      }
    ])

    console.log(students)


    // API 호출
    try {
      const res = await apiPost('teacher.php', {
        mode: 'studentInsert',
        data: students
      })

      if (res.result === 'SUCCESS') {
        alert('업로드 성공')
        window.location.reload()
      } else {
        alert(res.message || '업로드 실패')
      }
    } catch (error) {
      alert('서버 오류 발생')
      console.error(error)
    }
  }

  reader.readAsArrayBuffer(file)
}


const onClickUpload = () => {
