  historyTitle.value = type === 'tax' ? '세금 내역' : '벌금 내역'
  const res = await apiPost('bank.php', {
    mode: 'historyByType',
    idnt_code: sessionStorage.getItem('t_idnt_code'),
    type // 'tax' or 'penalty'
  })
  historyList.value = res.data || []
  isHistoryModalOpen.value = true
}



